mapper.select:
SELECT * FROM ( SELECT result1.*, ROWNUM AS rnum FROM ( SELECT pr.productId, pr.name, pr.price, pr.isActive, pr.codeBId, cb.name AS codeBName, pr.current_stock AS quantity -- 실시간 연산 제거 -> 컬럼 직접 조회 (O(1)) FROM product pr LEFT JOIN codeB cb ON pr.codeBId = cb.codeBId
ORDER BY
cb.name pr.name pr.price quantity pr.productId
DESC ASC
) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT result1.*, ROWNUM AS rnum FROM ( SELECT pr.productId, pr.name, pr.price, pr.isActive, pr.codeBId, cb.name AS codeBName FROM product pr LEFT JOIN codeB cb ON pr.codeBId = cb.codeBId
ORDER BY
cb.name pr.name pr.price pr.productId
DESC ASC
) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE product
SET codeBId=#{codeBId}, name=#{name}, price=#{price}, isActive=#{isActive},
	updatedAt=#{updatedAt}, note=#{note}

	, profileImage=#{profileImage}

WHERE productId=#{productId}

		UPDATE product
		SET 
        isActive = #{isActive}  
        WHERE productId = #{productId}
